LiquidPart.Liquid:
{% assign customerParent = Content["alias:customers"].Content %}
{% assign customers = Queries.CustomersQuery | query %}

<h2>{{ customerParent.TitlePart.Title }}</h2>
<div class="row">
{% for customer in customers %}
  <div class="col-lg-2 col-sm-4 mb-4">
     <img class="img-fluid" src="{{ customer.Content.Customer.Logo.Paths[0] | asset_url }}" alt="">
  </div>
{% endfor %}
 </div>
